package com.example.maggotpkmimport android.content.Contextclass SharedPreference(context: Context) {    companion object {        private const val IS_LOGIN = "is_login"        private const val PREF_NAME = "com.example.maggotpkm.preference"    }    private val preference = context.getSharedPreferences(PREF_NAME, Context.MODE_PRIVATE)    private val editor = preference.edit()    fun saveLogin(isLogin: Boolean) {        editor.apply {            putBoolean(PREF_NAME, isLogin)            apply()        }    }    fun getLogin(): Boolean {        return preference.getBoolean(PREF_NAME, false)    }}